<style lang="scss">
	.fade-transition {
		transition: opacity .6s ease .4s;
		opacity: 1;
	}
	.fade-enter, .fade-leave {
		opacity: 0;
	}
	.fade-leave {
		transition: opacity .1s linear
	}
	#navegacao {
		padding-top: 30px;
	}
	.fade2-transition {
		transition: opacity .4s ease;
		opacity: 1;
	}
	.fade2-enter, .fade2-leave {
		opacity: 0;
	}

	#janela2 {
		h3 {
			font-family: 'treta';
			color: #06303c;
		}
	}

	.mdl-layout__content {
		overflow: hidden;
	}

	.rodape {
		transition: bottom .3s;
		&:hover {
			bottom: 0;
			.footer_seta {
				transform: rotate(0deg);
			}
		}
		.footer_seta {
			transition: transform .3s;
			transform: rotate(180deg);
		}
	}

	.rwd_pinbox {
		cursor: pointer;
		&:hover {
			.rwd_pinicon {
				opacity: 1;
			}
		}
		&.filtered {
			border-bottom: 3px solid #06303c;
			.rwd_pinicon {
				opacity: 1;
			}
		}
		.rwd_pinicon {
			opacity: 0;
			transition: opacity .2s;
		}
		.mdl-button {
			background: transparent !important;
			font-size: 30px;
    	height: 35px;
    	min-width: 35px;
    	width: 35px;
		}
		.btnPin {
			font-size: 30px;
		}
	}

</style>

